import { Component } from '@angular/core';
import { NumtowordsService } from '../services/numtowords.service';

@Component({
  selector: 'app-numbertowords',
  templateUrl: './numbertowords.component.html',
})
export class NumbertowordsComponent {
  enteredNumber: Number = 0;
  wordsResult: string = '';

  constructor(private numtowordsService: NumtowordsService) {}

  handleOnConvert() {
    this.numtowordsService.getWords(this.enteredNumber).subscribe((words) => {
      this.wordsResult = words;
    });
  }
}
<input type="number" [(ngModel)]="enteredNumber" /><button
  type="button"
  onclick="handleonConvert"
>
  Convert to Words
</button>
<p>{{ wordsResult }}</p>
